import React, { useState } from 'react'
import Datepicker from "react-tailwindcss-datepicker";
import DropDown from '../dropDown/DropDown';

export default function SearchForm() {
    const [datePickerValue, setDatePickerValue] = useState({
        startDate: new Date()
    });
    const [search, setSearch] = useState("")
    const [category, setCategory] = useState("sports")
    const [source, setSource] = useState("")

    const handleValueChange = newValue => {
        console.log("newValue:", newValue);
        setDatePickerValue(newValue);
    };

    return (
        <>
            <div className="w-full">
                <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className="mb-2 grid grid-cols-1 gap-2 md:grid-cols-2 md:gap-2">
                        <input 
                            value={search}
                            onChange={(e) => setSearch(e.currentTarget.value)}
                            className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            type="text" 
                            placeholder="Search..."/>
                        <Datepicker 
                            containerClassName={""}
                            inputClassName={""}
                            primaryColor={"blue"}  
                            placeholder={"Search by date..."} 
                            displayFormat={"DD-MM-YYYY"} 
                            useRange={false} 
                            asSingle={true} 
                            value={datePickerValue} 
                            onChange={handleValueChange} />
                        <DropDown value={category} setvalue={setCategory} options={
                            [
                                {
                                    id: "sports",
                                    name: "Sports"
                                },
                                {
                                    id: "busienss",
                                    name: "Busienss"
                                }
                            ]
                        } />
                        <DropDown value={source} setvalue={setSource} options={
                            [
                                {
                                    id: 1,
                                    name: "The News"
                                },
                                {
                                    id: 2,
                                    name: "New York Times"
                                },
                                {
                                    id: 3,
                                    name: "The Guardians"
                                }
                            ]
                            } />
                    </div>
                    <div className="flex items-center justify-center">
                        <button className="mx-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button">
                            Search
                        </button>
                    </div>
                </form>
            </div>
        </>
    )
}
