import { createSlice } from '@reduxjs/toolkit'

const todayDate = () => {
    const date = new Date()
    return `${date.getDay()< 10 ? `0${date.getDay()}` : date.getMonth()}-${date.getMonth() + 1 < 10 ? `0${date.getMonth() + 1}` : date.getMonth() + 1}-${date.getFullYear()}`
}

export const searchParamSlice = createSlice({
  name: 'searchParam',
  initialState: {
    searchParam: {
        search: "tesla",
        date: todayDate(),
        category: "business",
        source: "all"
    }
  },
  reducers: {
    setSearchParam: (state, action) => { 
        return { 
            ...state,
            searchParam : action.payload 
        }
    },
  },
})

// Action creators are generated for each case reducer function
export const { setSearchParam } = searchParamSlice.actions

export default searchParamSlice.reducer
