import React, { useState } from 'react'
import Datepicker from "react-tailwindcss-datepicker";

export default function SearchForm() {
    const [datePickerValue, setDatePickerValue] = useState({
        startDate: new Date()
    });
    const [search, setSearch] = useState("")
    const [category, setCategory] = useState("")
    const [source, setSource] = useState("")

    const handleValueChange = newValue => {
        console.log("newValue:", newValue);
        setDatePickerValue(newValue);
    };

    return (
        <>
            <div className="w-full">
                <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className="mb-2 grid grid-cols-1 gap-2 md:grid-cols-2 md:gap-2">
                        <input 
                            value={search}
                            onChange={(e) => setSearch(e.currentTarget.value)}
                            className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            type="text" 
                            placeholder="Search..."/>
                        <Datepicker 
                            containerClassName={""}
                            inputClassName={""}
                            primaryColor={"blue"}  
                            placeholder={"Search by date..."} 
                            displayFormat={"DD-MM-YYYY"} 
                            useRange={false} 
                            asSingle={true} 
                            value={datePickerValue} 
                            onChange={handleValueChange} />
                        <div class="p-10">
                            <div class="dropdown inline-block relative">
                                <button class="bg-gray-300 text-gray-700 font-semibold py-2 px-4 rounded inline-flex items-center">
                                    <span class="mr-1">Dropdown</span>
                                    <svg class="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/> </svg>
                                </button>
                                <ul class="dropdown-menu absolute hidden text-gray-700 pt-1">
                                    <li class=""><a class="rounded-t bg-gray-200 hover:bg-gray-400 py-2 px-4 block whitespace-no-wrap" href="#">One</a></li>
                                    <li class=""><a class="bg-gray-200 hover:bg-gray-400 py-2 px-4 block whitespace-no-wrap" href="#">Two</a></li>
                                    <li class=""><a class="rounded-b bg-gray-200 hover:bg-gray-400 py-2 px-4 block whitespace-no-wrap" href="#">Three is the magic number</a></li>
                                </ul>
                            </div>
                        </div>
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Category"/>
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Source"/>
                    </div>
                    <div className="flex items-center justify-center">
                        <button className="mx-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button">
                            Search
                        </button>
                    </div>
                </form>
            </div>
        </>
    )
}
