import React, { useState } from 'react'
import Datepicker from "react-tailwindcss-datepicker";

export default function SearchForm() {
    const [value, setValue] = useState({
        startDate: new Date()
    });

    const handleValueChange = newValue => {
        console.log("newValue:", newValue);
        setValue(newValue);
    };
    return (
        <>
            <div className="w-full">
                <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className="mb-2 grid grid-cols-1 gap-2 md:grid-cols-2 md:gap-2">
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Search..."/>
                        <Datepicker 
                            inputClassName={"w-60 mx-2 shadow appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"}
                            primaryColor={"blue"}  
                            placeholder={"Search by date..."} 
                            displayFormat={"DD-MM-YYYY"} 
                            useRange={false} 
                            asSingle={true} 
                            value={value} 
                            onChange={handleValueChange} />
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Category"/>
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Source"/>
                    </div>
                    <div className="flex items-center justify-center">
                        <button className="mx-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button">
                            Search
                        </button>
                    </div>
                </form>
            </div>
        </>
    )
}
