import React, { useState } from 'react'
import Datepicker from "react-tailwindcss-datepicker";

export default function SearchForm() {
    const [datePickerValue, setDatePickerValue] = useState({
        startDate: new Date()
    });
    const [search, setSearch] = useState("")

    const handleValueChange = newValue => {
        console.log("newValue:", newValue);
        setDatePickerValue(newValue);
    };

    return (
        <>
            <div className="w-full">
                <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <div className="mb-2 grid grid-cols-1 gap-2 md:grid-cols-2 md:gap-2">
                        <input 
                            value={search}
                            onChange={(e) => setSearch(e.currentTarget.value)}
                            className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" 
                            type="text" 
                            placeholder="Search..."/>
                        <Datepicker 
                            containerClassName={""}
                            inputClassName={""}
                            primaryColor={"blue"}  
                            placeholder={"Search by date..."} 
                            displayFormat={"DD-MM-YYYY"} 
                            useRange={false} 
                            asSingle={true} 
                            value={datePickerValue} 
                            onChange={handleValueChange} />
                        <div class="relative inline-block text-left">
                            <div>
                                <button type="button" class="inline-flex w-full justify-center gap-x-1.5 rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50" id="menu-button" aria-expanded="true" aria-haspopup="true">
                                Options
                                <svg class="-mr-1 h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                    <path fill-rule="evenodd" d="M5.23 7.21a.75.75 0 011.06.02L10 11.168l3.71-3.938a.75.75 0 111.08 1.04l-4.25 4.5a.75.75 0 01-1.08 0l-4.25-4.5a.75.75 0 01.02-1.06z" clip-rule="evenodd" />
                                </svg>
                                </button>
                            </div>

                            <div class="hidden right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
                                <div class="py-1" role="none">
                                <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-0">Account settings</a>
                                <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-1">Support</a>
                                <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-2">License</a>
                                <form method="POST" action="#" role="none">
                                    <button type="submit" class="text-gray-700 block w-full px-4 py-2 text-left text-sm" role="menuitem" tabindex="-1" id="menu-item-3">Sign out</button>
                                </form>
                                </div>
                            </div>
                        </div>
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Category"/>
                        <input className="shadow mx-2 appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" type="text" placeholder="Source"/>
                    </div>
                    <div className="flex items-center justify-center">
                        <button className="mx-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="button">
                            Search
                        </button>
                    </div>
                </form>
            </div>
        </>
    )
}
